INSERT INTO `fox_images`(`id`, `column_id`, `column`, `title`, `tags`, `brief_title`, `picset_ids`, `pic_ids`, `breviary_pic_id`, `click`, `article_field`, `keywords`, `description`, `article_source`, `author`, `content`, `team_status`, `release_time`, `lang`, `create_time`, `update_time`) VALUES (58,20,'图集案例','计算机主板设计','','主板设计','332,333,334,335,','',336,3256,'','计算机主板设计方案','数字工业计算机嵌入入产品提供商，行业经验丰富，可按需定制，品质有保障。','黔狐科技','FoxCMS','<p><strong>电脑主板PCB设计特点：</strong></p>\n<p>1、FSB总线速率800M，严格的分组及时序关系；</p>\n<p>2、8片DDRII 800M，要求一面贴，高密度布局，严格的拓扑结构和时序关系；</p>\n<p>3、多路开关电源，在有限的空间内合理布局，很好的满足热设计，有效的降低电源噪声；</p>\n<p>4、各个功能模块合理紧凑的布局，提高了信号质量，降低信号间的串扰；</p>\n<p>5、采用标准的CPCI卡槽。</p>\n<p>&nbsp;</p>\n<p><strong>PCB制板参数：</strong></p>\n<p>PCB层数：8层</p>\n<p>PCB表面：沉金工艺</p>\n<p>PCB板材：Htg170, 1oz</p>\n<p>表面处理：无铅喷锡</p>\n<p>测试方式：100%电测，阻抗控制</p>',',','2023-03-14 10:47:00','cn','2023-03-14 10:49:37','2023-05-09 21:58:34');
