INSERT INTO `fox_product`(`id`, `column`, `column_id`, `tags`, `title`, `brief_title`, `picset_ids`, `pic_ids`, `breviary_pic_id`, `click`, `article_field`, `keywords`, `description`, `status`, `article_source`, `author`, `content`, `team_status`, `release_time`, `lang`, `create_time`, `update_time`, `price`, `buy_link`, `big_img`) VALUES (23,'智能硬件',18,'','妙控键盘双面夹','键盘','29,29,','',327,0,'c','妙控键盘','用它打字感觉超赞，内置触控板助你精准操控，执行各项任务，还配备了一列 14 个功能键。',1,'','','<h3 style=\"margin: 0px; padding: 0px; color: #1d1d1f; quotes: \'&ldquo;\' \'&rdquo;\'; font-family: \'SF Pro SC\', \'SF Pro Text\', \'SF Pro Icons\', \'AOS Icons\', \'PingFang SC\', \'Helvetica Neue\', Helvetica, Arial, sans-serif; background-color: #ffffff;\">适用于 iPad (第十代) 的妙控键盘双面夹</h3>\n<p>一位用户坐在桌前，使用妙控键盘双面夹在 iPad 上打字，这款键盘内置触控板，并连有一片背板。用户点按音量增大按键。随后镜头剪接，画面中的用户将背板向后收折，用手托着 iPad 和妙控键盘双面夹。用户将 iPad 从键盘上取下，双手拿着 iPad 和背板，将键盘留在桌上。用户调整妙控键盘双面夹，将 iPad 立在桌上。用户合上妙控键盘双面夹，将 iPad 贴合地收起并留在桌上。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"//oss.foxcms.cn/demo/files/20230506/d61785128fbef070.jpg\" data-id=\"327\" /></p>',',','2023-03-16 11:33:00','cn','2023-05-06 00:56:56','2024-05-15 10:36:15','4532','//www.apple.com.cn/shop/','//oss.foxcms.cn/demo/files/20230311/fa079350d03148c8.jpg');
